- scanCharactersFromSet:intoString:
- scanUpToCharactersFromSet:intoString:
- scanDecimal:
- scanDouble:
- scanFloat:
- scanHexDouble:
- scanHexFloat:
- scanHexInt:
- scanHexLongLong:
- scanInteger:
- scanInt:
- scanLongLong:
- scanString:intoString:
- scanUnsignedLongLong:
