       [queue addOperationWithBlock:^{
           sleep(1);//保证此线程后面的方法后调用
           NSLog(@"2 将要上锁");
           [lock lock];
           NSLog(@"2 已上锁，访问资源");
           name = @"康师傅";
           sleep(2);
           NSLog(@"2 将要解锁");
           [lock unlock];
           NSLog(@"2 已解锁");

       }];
   }
